function signOut() {
    try {
        if (gapi) {
            if (gapi.auth2) {
                var auth2 = gapi.auth2.getAuthInstance();
                if (auth2) {
                    auth2.signOut();
                    auth2.disconnect();
                }
            }
        }
    } catch (e) {
        console.log("Error in logging out from gmail")
    }
}

