    function unEscapeRegHtml(unsafe) {
        if(!unsafe){
            return "";
        }
        return unsafe
            .replace(/&amp;/g, "&")
            .replace(/&lt;/g, "<")
            .replace(/&gt;/g, ">")
            .replace(/&quot;/g, "\"")
            .replace(/&reg;/g, "")
            .replace(/&lsquo;/g, "")
            .replace(/&rsquo;/g, "")
            .replace(/&ndash;/g, "")
            .replace(/&#039;/g, "'");
    }

