function topNewsStack(source, data, newsRows, newsColumns) {
    var template = Handlebars.compile(source);
    var table = [], newRow;
    var dataIdx = 0;
    for (r = 0; r < newsRows; r++) {
        newRow = [];
        for (c = 0; c < newsColumns; c++) {
            newRow.push(data[dataIdx]);
            dataIdx++;
        }
        table.push(newRow);
    }
    $('.news-stack').append(template(table));
}

